package com.eightgamesolver.algorithms;

import com.eightgamesolver.exceptions.Exceptions;
import com.eightgamesolver.utils.ProcessInput;
import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.function.Executable;

import java.io.File;
import java.io.FileInputStream;
import java.io.FileNotFoundException;
import java.io.InputStream;

class DFSTest {

    @Test
    void dfsConfig1() throws Exceptions.InvalidStates, FileNotFoundException, Exceptions.InvalidPath {
        File initialFile = new File("src/test/resources/validConfig");
        final InputStream inputFromFile = new FileInputStream(initialFile);
        System.setIn(inputFromFile);
        ProcessInput processInput = new ProcessInput();
        int[] initialState = processInput.getInitialState();
        int[] finalState = processInput.getFinalState();
        String solutionPath = new DFS().getSolutionPath(initialState, finalState);
        Assertions.assertEquals(
            "",
            solutionPath);
        Assertions.assertEquals(17463, solutionPath.length());
    }

    @Test
    void dfsConfig2() throws Exceptions.InvalidStates, FileNotFoundException {
        File initialFile = new File("src/test/resources/validConfig2");
        final InputStream inputFromFile = new FileInputStream(initialFile);
        System.setIn(inputFromFile);
        ProcessInput processInput = new ProcessInput();
        int[] initialState = processInput.getInitialState();
        int[] finalState = processInput.getFinalState();
        Assertions.assertThrows(OutOfMemoryError.class, () -> new DFS().getSolutionPath(initialState, finalState));
    }
}
